CREATE TABLE IF NOT EXISTS `cn_province` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` bigint DEFAULT NULL COMMENT '父节点id',
  `parent_path` json DEFAULT NULL COMMENT '父节点路径id',
  `level_type` smallint unsigned DEFAULT NULL COMMENT '等级类型 310省 320自治区 410直辖市 420特别行政区',
  `next_level_type` char(6) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '下级等级类型',
  `name_cn` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '中文名',
  `name_en` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '英文名',
  `name_other` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '其他语言名',
  `name_pinyin` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拼音 - 全拼',
  `name_jianpin` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拼音 - 简拼',
  `region_code` varchar(6) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '行政区代码、邮编',
  `phone_code` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '手机号区号',
  `zone` tinyint(1) DEFAULT NULL COMMENT '区域 1北半球国家、北方城市 2南半球国家、南半球城市',
  `utc` varchar(6) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '时区',
  `lng` double DEFAULT NULL COMMENT '经度',
  `lat` double DEFAULT NULL COMMENT '维度',
  `capital` tinyint(1) DEFAULT '0' COMMENT '是否是首都、省会',
  `osm_id` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT 'openstreetmap id',
  `geo_names_id` int DEFAULT NULL COMMENT 'geonames id',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `idx_province_name_cn` (`parent_id`,`name_cn`) USING BTREE,
  KEY `idx_province_name_en` (`parent_id`,`name_en`) USING BTREE,
  KEY `idx_province_name_other` (`parent_id`,`name_other`) USING BTREE,
  KEY `idx_province_name_pinyin` (`parent_id`,`name_pinyin`) USING BTREE,
  KEY `idx_province_name_jianpin` (`parent_id`,`name_jianpin`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=28309509700050218 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='中国 - 300 - 省级（310省 320自治区 410直辖市 420特别行政区）';

